function sidemenu(){const menu=document.getElementById("sidemenu");menu.classList.toggle("sidemenu")}document.addEventListener("DOMContentLoaded",(function(){const listItems=document.querySelectorAll(".profieldropdown ul li");const textvakken=document.querySelectorAll('main > div[class^="textvak"]');listItems.forEach((function(item,index){item.addEventListener("click",(function(){document.querySelector(".profielactive").classList.remove("profielactive");this.classList.add("profielactive");textvakken.forEach((function(textvak){textvak.style.display="none"}));textvakken[index].style.display="block"}))}))}));const filterBtn=document.getElementById("filterBtn");if(filterBtn){filterBtn.addEventListener("click",(()=>{const filterSection=document.querySelector(".filter");if(filterSection.style.display==="none"){filterSection.style.display="block"}else{filterSection.style.display="none"}}))}function laadVerzoeken(verzoeken){const list=document.getElementById("verzoekenList");if(!list){console.error("Element met id 'verzoekenList' niet gevonden op de pagina.");return}verzoeken.forEach((verzoek=>{const verzoekItem=document.createElement("li");verzoekItem.innerHTML=` <img src="/uploads/${verzoek.dierFoto}" alt=""> \n      <div>\n        <h2>${verzoek.dierNaam}</h2>\n        <p>${verzoek.zoekerNaam}</p>\n        <p>${verzoek.status}</p>\n      </div>\n      <form action="/accepteren" method="post">\n        <input type="hidden" name="verzoekId" value="${verzoek._id}">\n        <button type="submit" name="accepteren" value="accepteren">‚úì</button>\n        <button type="submit" name="accepteren" value="weigeren">ê§ï</button>\n      </form> `;list.appendChild(verzoekItem)}))}document.addEventListener("DOMContentLoaded",(function(){const listItems=document.querySelectorAll(".profieldropdown ul li");const textvakken=document.querySelectorAll('main > div[class^="textvak"]');if(listItems){listItems.forEach((function(item,index){item.addEventListener("click",(function(){document.querySelector(".profielactive").classList.remove("profielactive");this.classList.add("profielactive");textvakken.forEach((function(textvak){textvak.style.display="none"}));textvakken[index].style.display="block"}))}))}}));function showpassword(x){x.classList.toggle("fa-regular");x.classList.toggle("fa-solid");x.classList.toggle("fa-eye");x.classList.toggle("fa-eye-slash");const inputElement=document.getElementById("wachtwoord");if(inputElement.type==="password"){inputElement.type="text"}else{inputElement.type="password"}}function showrepassword(x){x.classList.toggle("fa-regular");x.classList.toggle("fa-solid");x.classList.toggle("fa-eye");x.classList.toggle("fa-eye-slash");const inputElement=document.getElementById("repassword");if(inputElement.type==="password"){inputElement.type="text"}else{inputElement.type="password"}}document.addEventListener("DOMContentLoaded",(function(){const passwordField=document.getElementById("wachtwoord");const repasswordField=document.getElementById("repassword");const errorSpan=document.getElementById("pass");const form=document.querySelector(".registreerform");if(repasswordField){repasswordField.addEventListener("input",(function(){if(passwordField.value!==repasswordField.value){errorSpan.textContent="De wachtwoorden komen niet overeen"}else{errorSpan.textContent=""}}))}if(form){form.addEventListener("submit",(function(event){if(passwordField.value!==repasswordField.value){errorSpan.textContent="De wachtwoorden komen niet overeen";event.preventDefault()}}))}}));document.addEventListener("DOMContentLoaded",(function(){const items=document.querySelectorAll(".slider .item");let active=-1;const next=document.getElementById("next");const prev=document.getElementById("prev");const submitButton=document.querySelector(".vragenlijstbtn");function loadShow(){items[active].style.transform="none";items[active].style.zIndex=1;items[active].style.filter="none";items[active].style.opacity=1;let stt=0;for(var i=active+1;i<items.length;i++){stt++;items[i].style.transform=`translateX(${120*stt}px) scale(${1-.2*stt}) perspective(16px) rotateY(-1deg)`;items[i].style.zIndex=-stt;items[i].style.filter="blur(5px)";items[i].style.opacity=stt>2?0:.6}stt=0;for(var i=active-1;i>=0;i--){stt++;items[i].style.transform=`translateX(${-120*stt}px) scale(${1-.2*stt}) perspective(16px) rotateY(1deg)`;items[i].style.zIndex=-stt;items[i].style.filter="blur(5px)";items[i].style.opacity=stt>2?0:.6}}function toggleButtonsVisibility(){if(active===0){prev.style.display="none"}else{prev.style.display="block"}if(active===items.length-1){next.style.display="none";submitButton.style.display="block"}else{next.style.display="block";submitButton.style.display="none"}}if(prev&&next&&submitButton){toggleButtonsVisibility()}if(next){next.onclick=function(){active=active+1<items.length?active+1:active;loadShow();toggleButtonsVisibility()}}if(prev){prev.onclick=function(){active=active-1>=0?active-1:active;loadShow();toggleButtonsVisibility()}}}));const swiperElement=document.querySelector(".swiper");if(swiperElement){const swiper=new Swiper(swiperElement,{pagination:{el:".swiper-pagination"},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},scrollbar:{el:".swiper-scrollbar"}})}const createPopup=id=>{const popupNode=document.querySelector(id);if(!popupNode){console.error(`Element met id '${id}' niet gevonden op de pagina.`);return}const overlay=popupNode.querySelector(".overlay");const closeBtn=popupNode.querySelector(".close-btn");const openPopup=()=>{popupNode.classList.add("active")};const closePopup=()=>{popupNode.classList.remove("active")};overlay.addEventListener("click",closePopup);closeBtn.addEventListener("click",closePopup);return openPopup};document.addEventListener("DOMContentLoaded",(()=>{const popupOpener=createPopup("#popup");if(popupOpener){document.querySelector("#open-popup").addEventListener("click",popupOpener)}}));